<?php
/**
 * 拦截器方法
 * $value array
 * $rule array
 * return false,$preg
 */
function intercept($value,$rule){
    if(!is_array($rule))return false;
    if(is_array($value)){
        foreach ($value as $key => $v){
            foreach ($rule as $r){
                if(preg_match('/'.$r.'/is',$v,$preg)){
                    $result = [
                        'key' => $key,
                        'value' => $v,
                        'rule' => $r,
                        'preg' => $preg,
                        'datetime' => date("Y-m-d H:i:s"),
                        'ip' => real_ip()
                    ];
                    log_add($result);
                    return $result;
                }
            }
        }
        return false;
    }else{
        return false;
    }
}

function log_add($result){
    $date = date('Y-m-d');
    $result = [$result];
    if(file_exists(WAFDIR.'log/'.$date.'.log')){
        $log = file_get_contents(WAFDIR.'log/'.$date.'.log');
        $log = json_decode($log,true);
        $data = json_encode(array_merge_recursive($log,$result));
        file_put_contents(WAFDIR.'log/'.$date.'.log',$data);
    }else{
        $data = json_encode($result);
        file_put_contents(WAFDIR.'log/'.$date.'.log',$data);
    }
}

